import React, { useEffect, useState } from 'react'

export default function () {

    const createContent = async()=>{
        let content= document.getElementById("content").value;
        let title= document.getElementById("title").value;
        let jsonData={
            'title':title,
            'content':content
        }
        let response = fetch('http://localhost:4000/api/create',{
            method: 'POST',
            headers: {
                    'Content-Type': 'application/json' 
                },
             body: JSON.stringify(jsonData) 
        })
        .then(response => {
            // Check if the request was successful
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            // Parse the response as JSON
            return response.json();
          })
          .then(data => {
            alert("data entered");
            // Handle the JSON data
            console.log(data);
          })
          .catch(error => {
            // Handle any errors that occurred during the fetch
            console.error('Fetch error:', error);
          });
    }

    const [datann,setdata]=useState();
    const call= async()=>{
        let data= await fetch('http://localhost:4000/api/posts');
        let datan= await data.json();
        setdata(datan.data);
        console.log(datan.data);
    };

    useEffect(()=>{
        call();
    },[])

     return (
        <div>
        <form onSubmit={createContent}>
            <input type="text" placeholder='title' id="title"></input>
            <textarea type="text" placeholder='content' id="content"></textarea>
            <input type="submit"></input>
        </form>

        <table border="1">
            {
                datann?.map((element)=>(
                    <tr>
                    <td>{element.content}</td>
                    <td>{element.title}</td>
                    <td><button>Delete</button></td>
                    </tr>
                ))
            }
        </table>


        </div>
        )
}
